<template>
  <div class="search">
    <div class="search-box">
      <img src="data:image/svg+xml;base64," alt="">
      <input type="text" placeholder="搜索歌曲、歌手、专辑" v-model="keywords" v-throttle="search" >
    </div>
    <div class="search-suggest" v-show="keywords !== ''">
      <IScroll ref="scrollView">
        <ul>
          <li v-for="value in songs" :key="value.id" @click="play(value.id)">
            <img src="data:image/svg+xml;base64," alt="">
            <p>{{value.name}}-{{value.artists[0].name}}</p>
          </li>
        </ul>
      </IScroll>
    </div>
    <div class="search-hot">
      <h3>热门搜索</h3>
      <ul>
        <li v-for="(value ,index) in hots" :key="index" @click="hotClick(value.first)">{{value.first}}</li>
      </ul>
    </div>
    <ul class="search-history">
      <li v-for="value in history" :key="value">
        <div class="history-left">
          <img src="data:image/svg+xml;base64," alt="">
          <p>{{value}}</p>
        </div>
        <div class="history-right">
          <img @click.stop="delHistory(value)" src="data:image/svg+xml;base64," alt="">
        </div>
      </li>
    </ul>
  </div>
</template>

<script>

import IScroll from "../components/ScrollView"
import {getSearch , getSearchHot} from '../api/index'
import {mapActions} from 'vuex'
export default {
  name: "Search",
  components:{
    IScroll
  },
  data(){
    return{
      keywords: '',
      songs:[],
      hots:[],
      history:[]
    }
  },
  methods:{
    ...mapActions([
      'setFullScreen',
      'setSongDetail',
      'setIsPlaying'
    ]),
    search(){
      if(this.keywords !== ''){
        getSearch({"keywords":this.keywords})
          .then((data) => {
            this.songs = data.result.songs
          })
          .catch(function (err) {
            console.log(err);
          })
      }
    },
    play(id){
      this.setFullScreen(true);
      this.setSongDetail([id]);

      if (this.history.includes(this.keywords)) {
        return
      }
      this.history.push(this.keywords)
      window.localStorage.setItem('searchHistory',JSON.stringify(this.history))
    },
    hotClick(first){
      this.keywords = first;
      this.search()
    },
    delHistory(value){
      this.history = this.history.filter(function (item) {
        return item !== value
      })
      window.localStorage.setItem('searchHistory',JSON.stringify(this.history))
    }
  },
  watch:{
    keywords(){
      this.$refs.scrollView.refresh();
    },
  },
  created() {
    getSearchHot()
      .then((data) => {
        this.hots = data.result.hots
      })
    .catch(function (err) {
      console.log(err);
    })

    let history = JSON.parse(window.localStorage.getItem('searchHistory'))
    if(history === null || history === undefined){
      return
    }
    this.history = history
  },
  directives: {
    throttle: {
      // 指令的定义
      inserted: function (el, obj) {
        let timerId = null
        let flag = true
        el.addEventListener('input', function () {
          if (!flag) return
          flag = false
          timerId && clearTimeout(timerId)
          timerId = setTimeout(function () {
            flag = true
            obj.value()
          }, 1000)
        })
      }
    }
  },
}
</script>

